import React, { useState, useEffect } from 'react';
import AudioPlayer from './AudioPlayer';
import Playlist from './Playlist';

const App = () => {
  const [audioList, setAudioList] = useState([
    { title: 'Song 1', url: 'path/to/song1.mp3' },
    { title: 'Song 2', url: 'path/to/song2.mp3' },
    // Add more audio files as needed
  ]);

  const [currentIndex, setCurrentIndex] = useState(0);

  const handleAudioSelect = (index) => {
    setCurrentIndex(index);
  };

  const handleAudioEnd = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % audioList.length);
  };

  useEffect(() => {
    // Save current playing index to local storage
    localStorage.setItem('lastPlayingIndex', currentIndex.toString());
  }, [currentIndex]);

  useEffect(() => {
    // Load last playing index from local storage
    const lastPlayingIndex = localStorage.getItem('lastPlayingIndex');
    if (lastPlayingIndex !== null) {
      setCurrentIndex(parseInt(lastPlayingIndex, 10));
    }
  }, []);

  return (
    <div>
      <AudioPlayer
        audioList={audioList}
        currentIndex={currentIndex}
        onEnd={handleAudioEnd}
      />
      <Playlist
        audioList={audioList}
        currentIndex={currentIndex}
        onAudioSelect={handleAudioSelect}
      />
    </div>
  );
};

export default App;
